+@import url("https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap");
+
+@layer base {
+  body {
+    font-family: 'Roboto', sans-serif;
+    background-color: black;
+    color: white;
+  }
+  
+  .h1 {
+    @apply text-[48px] xl:text-[80px] leading-[1.1] font-semibold;
+}
+  .h2 {
+    @apply text-[36px] xl:text-[48px] leading-[1.1] font-semibold;
+  }
+  .h3 {
+    @apply text-[28px] xl:text-[24px] leading-[1.1] font-semibold;
+  }
+
+  .text-outline {
+    -webkit-text-stroke: 1px #ffffff;
+  }
+  
+  .text-outline:hover {
+    -webkit-text-stroke: 1px #9615db;
+  }
+  
+    .tags {
+      background-color: #540d68e2;
+      margin-right: 50px;
+      margin-top: 0.5rem;
+      padding: 7px 25px;
+      display: inline-block;
+      color: #ffffff;
+      font-size: 12px;
+      margin-bottom: 8px;
+}
+
+    .tags:last-child {
+      margin-right: 0;
+    };
+  };
